import React, { useState, useEffect } from "react";

import { useStyles } from "./Product.styles";

import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import IProduct from "../../models/ProductModel";
import ISeller from "../../models/SellerModel";
import { apiSellerOnly } from "../../apiService";

import { Link } from "react-router-dom";
import { Dialog } from "material-ui";
import { DialogTitle, DialogContent } from "@material-ui/core";

export default function Product(props: {
  product: IProduct;
  addOrRemove: string;
  actionFunction: (prod: IProduct) => void;
}) {
  const classes = useStyles();
  const [seller, setSeller] = useState({} as ISeller);
  useEffect(() => {
    apiSellerOnly(props.product.seller.id)
      .then(res => res.json())
      .then(res => {
        if (res.seller) {
          setSeller(res.seller);
        }
      });
  }, [props.product.seller.id]);
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Card className={classes.card}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={props.product.thumbnail}
          component={Link}
          to="/detail"
        />
        <CardContent>
          <Typography className={classes.notOverflow}>
            {props.product.title}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {props.product.price}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {seller.nickname}
          </Typography>
          <Typography
            variant="body2"
            color="textSecondary"
            component="p"
          ></Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button
          size="large"
          color="primary"
          onClick={event => {
            props.actionFunction(props.product);
          }}
        >
          {props.addOrRemove}
        </Button>
      </CardActions>
      <Dialog
        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="responsive-dialog-title"
      >
        <DialogTitle id="responsive-dialog-title">
          {"Use Google's location service?"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Let Google help apps determine location. This means sending
            anonymous location data to Google, even when no apps are running.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="primary">
            Disagree
          </Button>

          <Button onClick={handleClose} color="primary" autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
    </Card>
  );
}
